<?phprequire('../common.php');$idItem = (int)filter_input(INPUT_POST, 'lavoro_id', FILTER_SANITIZE_NUMBER_INT);$data_inizio_lav = date("Y-m-d", strtotime(filter_input(INPUT_POST, 'data_inizio_lav', FILTER_SANITIZE_STRING)));$data_fine_lav = date("Y-m-d", strtotime(filter_input(INPUT_POST, 'data_fine_lav', FILTER_SANITIZE_STRING)));if ($data_fine_lav == '1970-01-01') $data_fine_lav = NULL;$datiLavoro = [    'id' => $idItem,    'cv_id' => filter_input(INPUT_POST, 'cv_id', FILTER_SANITIZE_NUMBER_INT),    'data_inizio_lav' => $data_inizio_lav,    'data_fine_lav' => $data_fine_lav,    'mansione' => filter_input(INPUT_POST, 'mansione', FILTER_DEFAULT),    'azienda' => filter_input(INPUT_POST, 'azienda', FILTER_DEFAULT),    'citta_azienda' => filter_input(INPUT_POST, 'citta_azienda', FILTER_DEFAULT),    'provincia_azienda' => filter_input(INPUT_POST, 'provincia_azienda', FILTER_SANITIZE_STRING),    'descrizione_mansione' => filter_input(INPUT_POST, 'descrizione_mansione', FILTER_DEFAULT),    'tipologia' => 'lavoro'];if ($idItem == 0){    $savedData = $curriculum->newLavoro($datiLavoro);} else {    $savedData = $curriculum->updateLavoro($datiLavoro);}echo json_encode($savedData);